const seen = new Set();
const uniqueItems = [];

for (const item of $input.all()) {
  const key = `${item.json.prompt1}||${item.json.prompt2}`;
  if (!seen.has(key)) {
    seen.add(key);
    uniqueItems.push(item);
  }
}

return uniqueItems;

// Get the object from your input
const content = $input.first().json.output;

// Define the list of allowed keys
const terms = [
  "dwc:scientificName",
  "dwc:catalogNumber",
  "dwc:recordNumber",
  "dwc:recordedBy",
  "dwc:year",
  "dwc:month",
  "dwc:day",
  "dwc:dateIdentified",
  "dwc:identifiedBy",
  "dwc:verbatimIdentification",
  "dwc:country",
  "dwc:countryCode",
  "dwc:decimalLatitude",
  "dwc:decimalLongitude",
  "dwc:locality",
  "dwc:minimumElevationInMeters",
  "dwc:maximumElevationInMeters",
  "dwc:verbatimElevation"
];

// Create a new object with only the key-value pairs where the key is in the terms list
const filteredContent = Object.keys(content)
  .filter(key => terms.includes(key))
  .reduce((acc, key) => {
    acc[key] = content[key];
    return acc;
  }, {});

// Return the filtered object
return { json: filteredContent };

